            if ( seeking ){
                jwplayer().seek(localStorage['position_' + episode_id]);
                $('#continue').fadeIn(300);
                setTimeout(function(){
                    $('#continue').remove();
                }, 6000);
            }
        });
    }
    jwplayer().onTime(function(){
        localStorage.setItem('position_' + episode_id, this.getPosition());
    });
    jwplayer().onComplete(function(){
